import React ,{useEffect,useState} from 'react';
import Recipies from './Recipies'

const App = () => {

    const API_KEY = 'da741b1671567b84363072abb8515889';
    const API_ID='be1aac05';
    const [data,setData] =useState([]);
    useEffect(() => {
        getData();
    }, [])

    const getData = () => {
        fetch(`https://api.edamam.com/search?q=chicken&app_id=c29a7bfc&app_key=c23fc3546139daf148326e75b93dc93d&from=0&to=3&calories=591-722&health=alcohol-free`).then(res => res.json())
        .then(data => {
            console.log(data.hits)
            setData(data.hits)
        })
        .catch(err => console.log(err))
    }

    return(
        <>
            <div className="App">
                <form>
                    <input type="text" placeholder="Enter item" />
                    <button type="submit">Search</button>
                </form>
                {
                    data.map((item,index) => {
                        return(
                            <Recipies
                                key={index}
                                title = {item.recipe.label}
                                photo = {item.recipe.image}
                                ingredient = {item.recipe.ingredientLines}

                            />
                        )
                    })
                }
            </div>
        </>
    )
}

export default App;
