import React ,{useEffect,useState} from 'react';
import Recipies from './Recipies'

const App = () => {

    const API_KEY = 'da741b1671567b84363072abb8515889';
    const API_ID='be1aac05';
    const [data,setData] =useState([]);
    const [search,setSearch] =useState("");
    const [query,setQuery] = useState("chicken");

    const updateSearch = () => {
        setQuery(search)
    }

    useEffect(() => {
        getData();
    }, [query])

    const getData = () => {
        fetch(`https://api.edamam.com/search?q=chicken&app_id=c29a7bfc&app_key=c23fc3546139daf148326e75b93dc93d&from=0&to=3&calories=591-722&health=alcohol-free`).then(res => res.json())
        .then(data => {
            console.log(data.hits)
            setData(data.hits)
        })
        .catch(err => console.log(err))
    }

    return(
        <>
            <div className="App" style={{backgroundColor:"grey"}}>
            <nav class="navbar navbar-expand-lg navbar-dark bg-dark">
                <a class="navbar-brand" href="#">Recipies</a>
                <button class="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
                    <span class="navbar-toggler-icon"></span>
                </button>

                <div class="collapse navbar-collapse" id="navbarSupportedContent">
                    <ul class="navbar-nav mr-auto">
                        <li class="nav-item active">
                            <a class="nav-link" href="#">Home <span class="sr-only">(current)</span></a>
                        </li>
                    </ul>
                    <form class="form-inline my-2 my-lg-0"
                    onSubmit={(e) => {
                        e.preventDefault()
                        updateSearch();

                    }}
                    
                    >
                        <input class="form-control mr-sm-2" type="search" placeholder="Search" aria-label="Search" 
                            value={search}
                            onChange={(e) => {
                                setSearch(e.target.value)
                            }}
                        />
                        <button class="btn btn-outline-success my-2 my-sm-0" type="submit"
                        >Search</button>
                    </form>
                </div>
            </nav>



                {
                    data.map((item,index) => {
                        return(
                            <Recipies
                                style={{display:"flex",justifyContent:"center",alignItems:"center"}}
                                key={index}
                                title = {item.recipe.label}
                                photo = {item.recipe.image}
                                ingredient = {item.recipe.ingredientLines}

                            />
                        )
                    })
                }
            </div>
        </>
    )
}

export default App;
